#!/usr/bin/python3

def to_number(message):
    h_message = bytes(message, 'utf-8').hex()
    return int(h_message, 16)

def encrypt(message, key):
    exponent, modulus = key
    plaintext = to_number(message)
    return pow(plaintext, exponent, modulus)

